mapper.select:
SELECT a.NUM AS num , a.FBI_IDX AS fbiIdx , a.MI_ID AS miId , a.FBI_TITLE AS fbiTitle , a.FBI_CONTENT AS fbiContent , a.FBI_READCNT AS fbiReadcnt , a.FBI_ISVIEW AS fbiIsview , a.FBI_REGTIME AS fbiRegtime , a.FBI_UPDATETIME AS fbiUpdatetime , a.FBI_DELETETIME AS fbiDeletetime , b.MI_NICKNAME AS miNickname FROM (SELECT ROW_NUMBER() OVER(ORDER BY fbi_idx DESC) AS NUM, page_a.* FROM T_FREEBOARD_INFO page_a ,T_MEMBER_INFO page_b WHERE page_a.MI_ID = page_b.MI_ID
AND page_a.FBI_TITLE LIKE CONCAT('%', #{keyword}, '%')
AND page_b.MI_NICKNAME LIKE CONCAT('%', #{keyword}, '%')
) a, T_MEMBER_INFO b WHERE a.MI_ID = b.MI_ID AND a.FBI_ISVIEW = 'Y' AND NUM BETWEEN ${startPage} AND ${endPage} ORDER BY a.fbi_idx DESC;

mapper.update:

UPDATE 
	T_FREEBOARD_REPLY
SET 
	FBR_COMMENT		=	TRIM(#{fbrComment}),
	FBR_UPDATETIME	=	NOW()
WHERE
	FBI_IDX			=	#{fbiIdx}
AND
	FBR_IDX			=	#{fbrIdx}
AND
	FBR_STEP		= 	#{fbrStep}
AND
	FBR_LEVEL		= 	#{fbrLevel}
AND
	MI_ID			=	#{miId}


UPDATE 
	T_FREEBOARD_REPLY
SET 
	FBR_ISVIEW		=	"N",
	FBR_DELETETIME	=	NOW()
WHERE
	FBI_IDX			=	#{fbiIdx}
AND
	FBR_IDX			=	#{fbrIdx}
AND
	FBR_STEP		= 	#{fbrStep}
AND
	FBR_LEVEL		= 	#{fbrLevel}
AND
	MI_ID			=	#{miId}
